            var totalSlides = slick.slideCount;
            var dots = $('.slick-dots li');

            dots.hide();
            for (var i = Math.max(0, currentSlide - 1); i <= Math.min(totalSlides - 1, currentSlide + 1); i++) {
                dots.eq(i).show();
            }
        });

        // Trigger the 'afterChange' event after initialization to show the correct dots
        $slider.trigger('afterChange', [$slider.slick('getSlick'), 0]);
    </script>
@endpush
